// assignment-manager-ui/
// ├── public/
// │   ├── logo.png  (Add your logo image here)
// │   ├── index.html
// ├── src/
// │   ├── components/
// │   │   ├── Sidebar.js
// │   │   ├── Dashboard.js
// │   │   ├── Courses.js
// │   │   ├── Calendar.js
// │   │   ├── Groups.js
// │   │   ├── AssignmentWorkspace.js
