import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Courses from "./components/Courses";
import Calendar from "./components/Calendar";
import Groups from "./components/Groups";
import ExtensionModal from "./components/ExtensionModal";

const App = () => {
  const [showModal, setShowModal] = useState(false);

  return (
    <Router>
      <div className="flex h-screen">
        {/* Sidebar */}
        <Sidebar />

        {/* Main content */}
        <div className="flex flex-col flex-1 bg-dark">
          <Header onRequestExtension={() => setShowModal(true)} />
          <main className="p-6 overflow-auto">
            <Routes>
              <Route path="/" element={<Dashboard onRequestExtension={() => setShowModal(true)} />} />
              <Route path="/courses" element={<Courses />} />
              <Route path="/calendar" element={<Calendar />} />
              <Route path="/groups" element={<Groups />} />
            </Routes>
          </main>
        </div>

        {/* Modal for extension request */}
        {showModal && (
          <ExtensionModal onClose={() => setShowModal(false)} />
        )}
      </div>
    </Router>
  );
};

export default App;
